import React, { useState } from "react";
import { generateComments, getCodeExplanationSteps } from "./api/ai";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { oneDark } from "react-syntax-highlighter/dist/esm/styles/prism";

function App() {
  const [code, setCode] = useState("");
  const [result, setResult] = useState("");
  const [mode, setMode] = useState("comments");
  const [loading, setLoading] = useState(false);
  const [language, setLanguage] = useState("Auto"); // Auto-detect default
const [history, setHistory] = useState([]);       // Last N results

const handleSubmit = async () => {
  if (!code.trim()) return;
  setLoading(true);
  setResult("");

  try {
    const payload = { codeSnippet: code, language };
    let data;
    if (mode === "comments") {
      data = await generateComments(payload);
      setResult(data.comments);
    } else {
      data = await getCodeExplanationSteps(payload);
      setResult(data.explanation);
    }

    // Add to history
    setHistory(prev => [{ mode, language, code, result: data[mode==='comments'?'comments':'explanation'] }, ...prev.slice(0,4)]); // last 5 items
  } catch (err) {
    setResult("❌ Error generating response. Try again!");
    console.error(err);
  }

  setLoading(false);
};


  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 p-6 flex justify-center">
      <div className="w-full max-w-4xl bg-white rounded-2xl shadow-xl p-6 flex flex-col gap-4">
        <h1 className="text-3xl font-bold text-center text-blue-700 mb-4">
          AI Code Helper
        </h1>

        {/* Tabs */}
        <div className="flex justify-center gap-4 mb-4">
          <button
            onClick={() => setMode("comments")}
            className={`px-6 py-2 rounded-lg font-medium ${
              mode === "comments"
                ? "bg-blue-600 text-white shadow-md"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            Generate Comments
          </button>
          <button
            onClick={() => setMode("explain")}
            className={`px-6 py-2 rounded-lg font-medium ${
              mode === "explain"
                ? "bg-blue-600 text-white shadow-md"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            Explain Steps
          </button>
        </div>

        <select
  value={language}
  onChange={(e) => setLanguage(e.target.value)}
  className="border p-2 rounded mb-4"
>
  <option value="English">English</option>
  <option value="Hindi">Hindi</option>
  <option value="Gujarati">Gujarati</option>
</select>

        {/* Code Input */}
        <textarea
          className="w-full h-40 border border-gray-300 rounded-lg p-3 font-mono text-sm focus:ring-2 focus:ring-blue-400 focus:outline-none"
          placeholder="Paste your code here..."
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />

        {/* Submit Button */}
        <button
          onClick={handleSubmit}
          className="w-full py-3 bg-green-500 text-white font-bold rounded-lg shadow hover:bg-green-600 transition-colors"
        >
          {loading ? "⏳ Generating..." : "Submit"}
        </button>

        {/* Result Display */}
        <div className="bg-gray-100 p-4 rounded-lg min-h-[200px] overflow-auto">
          {loading ? (
            <div className="flex items-center gap-2">
              <div className="w-5 h-5 border-4 border-green-500 border-t-transparent rounded-full animate-spin"></div>
              Generating...
            </div>
          ) : result ? (
            <SyntaxHighlighter language="javascript" style={oneDark} wrapLines={true}>
              {result}
            </SyntaxHighlighter>
          ) : (
            "Result will appear here..."
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
