import { useState } from "react";
import { getCodeExplanationSteps } from "../services/api";

export default function TeachingMode() {
  const [code, setCode] = useState("");
  const [steps, setSteps] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleExplain = async () => {
    setLoading(true);
    const res = await getCodeExplanationSteps(code);

    // Gemini se usually bullet points aayenge
    const stepList = res.explanation.split("\n").filter(line => line.trim() !== "");
    setSteps(stepList);
    setLoading(false);
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-3">Code Explanation (Teaching Mode)</h2>
      
      <textarea
        value={code}
        onChange={(e) => setCode(e.target.value)}
        placeholder="Paste your code here..."
        className="w-full h-40 p-2 border rounded"
      />

      <button
        onClick={handleExplain}
        disabled={loading}
        className="mt-3 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        {loading ? "Explaining..." : "Explain in Steps"}
      </button>

      <div className="mt-5">
        {steps.length > 0 && (
          <ol className="list-decimal pl-5 space-y-2">
            {steps.map((s, i) => (
              <li key={i} className="bg-gray-100 p-2 rounded">
                {s}
              </li>
            ))}
          </ol>
        )}
      </div>
    </div>
  );
}
