import React, { useState } from "react";
import { generateComments, getCodeExplanationSteps } from "./api/ai";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { oneDark } from "react-syntax-highlighter/dist/esm/styles/prism";

function App() {
  const [code, setCode] = useState("");
  const [result, setResult] = useState("");
  const [mode, setMode] = useState("comments");
  const [loading, setLoading] = useState(false);
  const [language, setLanguage] = useState("Auto");
  const [history, setHistory] = useState([]);

  const handleDownload = () => {
    const element = document.createElement("a");
    const file = new Blob([result], { type: "text/plain" });
    element.href = URL.createObjectURL(file);
    element.download = `ai_output_${mode}_${language}.txt`;
    document.body.appendChild(element);
    element.click();
  };

  const handleSubmit = async () => {
    if (!code.trim()) return;
    setLoading(true);
    setResult("");

    try {
      const payload = { codeSnippet: code, language };
      let data;
      if (mode === "comments") {
        data = await generateComments(payload);
        setResult(data.comments);
      } else {
        data = await getCodeExplanationSteps(payload);
        setResult(data.explanation);
      }

      // Add to history
      setHistory(prev => [{ mode, language, code, result: data[mode==='comments'?'comments':'explanation'] }, ...prev.slice(0,4)]);
    } catch (err) {
      setResult("❌ Error generating response. Try again!");
      console.error(err);
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-800 p-6 flex justify-center items-center">
      <div className="w-full max-w-4xl bg-white/95 backdrop-blur-sm rounded-3xl shadow-2xl p-8 flex flex-col gap-6">
        {/* Header */}
        <div className="text-center">
          <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-2">
            AI Code Helper
          </h1>
          <p className="text-gray-600">Enhance your code with AI-powered comments and explanations</p>
        </div>

        {/* Tabs */}
        <div className="flex justify-center gap-2 p-1 bg-gray-100 rounded-xl">
          <button
            onClick={() => setMode("comments")}
            className={`px-6 py-3 rounded-xl font-medium transition-all duration-300 ${
              mode === "comments"
                ? "bg-white text-blue-600 shadow-md"
                : "text-gray-600 hover:text-blue-500"
            }`}
          >
            Generate Comments
          </button>
          <button
            onClick={() => setMode("explain")}
            className={`px-6 py-3 rounded-xl font-medium transition-all duration-300 ${
              mode === "explain"
                ? "bg-white text-blue-600 shadow-md"
                : "text-gray-600 hover:text-blue-500"
            }`}
          >
            Explain Steps
          </button>
        </div>

        {/* Language Selector */}
        <div className="relative">
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-xl appearance-none focus:ring-2 focus:ring-blue-400 focus:border-transparent bg-white pl-4 pr-10"
          >
            <option value="Auto">Auto-Detect Language</option>
            <option value="English">English</option>
            <option value="Hindi">Hindi</option>
            <option value="Gujarati">Gujarati</option>
          </select>
          <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
          </div>
        </div>

        {/* Code Input */}
        <div className="relative">
          <textarea
            className="w-full h-40 border border-gray-300 rounded-xl p-4 font-mono text-sm focus:ring-2 focus:ring-blue-400 focus:outline-none resize-none"
            placeholder="Paste your code here..."
            value={code}
            onChange={(e) => setCode(e.target.value)}
          />
          {code && (
            <button
              onClick={() => setCode("")}
              className="absolute top-3 right-3 p-1 text-gray-400 hover:text-gray-600 rounded-full bg-gray-100"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          )}
        </div>

        {/* Submit Button */}
        <button
          onClick={handleSubmit}
          disabled={loading}
          className={`w-full py-4 text-white font-bold rounded-xl shadow-lg transition-all duration-300 flex items-center justify-center ${
            loading 
              ? "bg-blue-400 cursor-not-allowed" 
              : "bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 hover:shadow-xl"
          }`}
        >
          {loading ? (
            <>
              <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
              Generating...
            </>
          ) : (
            "Submit"
          )}
        </button>

        {/* Result Display */}
        <div className="bg-gray-50 p-5 rounded-xl border border-gray-200 min-h-[200px] overflow-auto">
          <div className="flex justify-between items-center mb-3">
            <h3 className="font-semibold text-gray-700">
              {mode === "comments" ? "Generated Comments" : "Code Explanation"}
            </h3>
            {result && (
              <button
                onClick={() => navigator.clipboard.writeText(result)}
                className="text-sm text-blue-500 hover:text-blue-700 flex items-center"
              >
                <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 5H6a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2v-1M8 5a2 2 0 002 2h2a2 2 0 002-2M8 5a2 2 0 012-2h2a2 2 0 012 2m0 0h2a2 2 0 012 2v3m2 4H10m0 0l3-3m-3 3l3 3" />
                </svg>
                Copy
              </button>
            )}
          </div>
          
          {loading ? (
            <div className="flex items-center gap-2 text-gray-500">
              <div className="w-5 h-5 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
              Generating your {mode === "comments" ? "comments" : "explanation"}...
            </div>
          ) : result ? (
            <SyntaxHighlighter language="javascript" style={oneDark} wrapLines={true}>
              {result}
            </SyntaxHighlighter>
          ) : (
            <div className="text-gray-400 text-center py-10">
              <svg className="w-12 h-12 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
              <p>Your {mode === "comments" ? "commented code" : "code explanation"} will appear here</p>
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="flex gap-3">
          <button
            onClick={handleDownload}
            disabled={!result}
            className={`flex-1 py-3 rounded-xl font-medium flex items-center justify-center gap-2 ${
              result 
                ? "bg-purple-500 text-white hover:bg-purple-600" 
                : "bg-gray-200 text-gray-400 cursor-not-allowed"
            }`}
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" />
            </svg>
            Download .txt
          </button>
        </div>

        {/* History Panel */}
        <div className="mt-2 bg-gray-50 p-4 rounded-xl border border-gray-200 max-h-64 overflow-auto">
          <div className="flex items-center justify-between mb-3">
            <h2 className="font-bold text-gray-700 flex items-center">
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              History (Last 5)
            </h2>
            {history.length > 0 && (
              <button 
                onClick={() => setHistory([])}
                className="text-sm text-red-500 hover:text-red-700"
              >
                Clear all
              </button>
            )}
          </div>
          
          {history.length === 0 ? (
            <p className="text-gray-400 text-center py-4">No history yet</p>
          ) : (
            history.map((item, index) => (
              <div key={index} className="border-b border-gray-200 py-3 last:border-0">
                <div className="flex justify-between items-center mb-1">
                  <span className="text-xs font-medium px-2 py-1 rounded-full bg-blue-100 text-blue-700">
                    {item.mode} | {item.language}
                  </span>
                  <span className="text-xs text-gray-500">
                    {new Date().toLocaleTimeString()}
                  </span>
                </div>
                <pre className="text-xs whitespace-pre-wrap bg-white p-2 rounded-lg mt-1">
                  {item.result.substring(0, 100)}...
                </pre>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
