import axios from "axios";

const API_URL = "http://localhost:5000/api";

export const getCodeExplanationSteps = async (codeSnippet) => {
  const response = await axios.post(`${API_URL}/explain-steps`, { codeSnippet });
  return response.data;
};

export const generateComments = async (codeSnippet) => {
  const response = await axios.post(`${API_URL}/generate-comments`, { codeSnippet });
  return response.data;
};
