import React, { useState } from "react";
import { generateComments, getCodeExplanationSteps } from "./api/ai";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { oneDark } from "react-syntax-highlighter/dist/esm/styles/prism";

function App() {
  const [code, setCode] = useState("");
  const [result, setResult] = useState("");
  const [mode, setMode] = useState("comments");
  const [language, setLanguage] = useState("Auto");
  const [loading, setLoading] = useState(false);
  const [history, setHistory] = useState([]);

  const handleSubmit = async () => {
    if (!code.trim()) return;
    setLoading(true);
    setResult("");

    try {
      const payload = { codeSnippet: code, language };
      let data;
      if (mode === "comments") {
        data = await generateComments(payload);
        setResult(data.comments);
      } else {
        data = await getCodeExplanationSteps(payload);
        setResult(data.explanation);
      }

      setHistory(prev => [
        { mode, language, code, result: data[mode === "comments" ? "comments" : "explanation"] },
        ...prev.slice(0, 9)
      ]); // Last 10 items
    } catch (err) {
      setResult("❌ Error generating response. Try again!");
      console.error(err);
    }

    setLoading(false);
  };

  const handleDownload = () => {
    const element = document.createElement("a");
    const file = new Blob([result], { type: "text/plain" });
    element.href = URL.createObjectURL(file);
    element.download = `ai_output_${mode}_${language}.txt`;
    document.body.appendChild(element);
    element.click();
  };

  return (
    <div className="min-h-screen flex bg-gray-50">
      
      {/* Left Panel: Main Code + Result */}
      <div className="flex-1 p-6 flex flex-col gap-4">
        <h1 className="text-3xl font-bold text-blue-700 mb-4">AI Code Helper</h1>

        {/* Tabs */}
        <div className="flex gap-4 mb-4">
          <button
            onClick={() => setMode("comments")}
            className={`px-6 py-2 rounded-lg font-medium ${
              mode === "comments"
                ? "bg-blue-600 text-white shadow-md"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            Generate Comments
          </button>
          <button
            onClick={() => setMode("explain")}
            className={`px-6 py-2 rounded-lg font-medium ${
              mode === "explain"
                ? "bg-blue-600 text-white shadow-md"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            Explain Steps
          </button>
        </div>

        {/* Language Selector */}
        <select
          value={language}
          onChange={(e) => setLanguage(e.target.value)}
          className="border p-2 rounded w-40"
        >
          <option value="Auto">Auto-Detect</option>
          <option value="English">English</option>
          <option value="Hindi">Hindi</option>
          <option value="Gujarati">Gujarati</option>
        </select>

        {/* Code Input */}
        <textarea
          className="w-full h-60 border border-gray-300 rounded-lg p-3 font-mono text-sm focus:ring-2 focus:ring-blue-400 focus:outline-none"
          placeholder="Paste your code here..."
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />

        {/* Submit Button */}
        <button
          onClick={handleSubmit}
          className="w-full py-3 bg-green-500 text-white font-bold rounded-lg shadow hover:bg-green-600 transition-colors"
        >
          {loading ? "⏳ Generating..." : "Submit"}
        </button>

        {/* Result Display */}
        <div className="bg-gray-100 p-4 rounded-lg flex-1 overflow-auto">
          {loading ? (
            <div className="flex items-center gap-2">
              <div className="w-5 h-5 border-4 border-green-500 border-t-transparent rounded-full animate-spin"></div>
              Generating...
            </div>
          ) : result ? (
            <SyntaxHighlighter language="javascript" style={oneDark} wrapLines={true}>
              {result}
            </SyntaxHighlighter>
          ) : (
            "Result will appear here..."
          )}
        </div>

        {/* Download Button */}
        <button
          onClick={handleDownload}
          className="px-4 py-2 bg-purple-500 text-white rounded-lg hover:bg-purple-600 mt-2"
        >
          Download .txt
        </button>
      </div>

      {/* Right Panel: History Sidebar */}
      <div className="w-80 bg-white border-l border-gray-200 p-4 flex flex-col">
        <h2 className="text-lg font-bold mb-3">History (Last 10)</h2>
        <div className="flex-1 overflow-auto space-y-2">
          {history.map((item, index) => (
            <div key={index} className="border p-2 rounded shadow-sm hover:bg-gray-50">
              <p className="text-sm font-medium">{item.mode} | {item.language}</p>
              <pre className="text-xs whitespace-pre-wrap max-h-32 overflow-auto">{item.result}</pre>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
