import React, { useState } from "react";
import CommentGenerator from "./components/CommentGenerator";
import CodeExplainer from "./components/CodeExplainer";

function App() {
  const [mode, setMode] = useState("comments");

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">AI Code Assistant</h1>

      {/* Mode Switcher */}
      <div className="mb-4 flex gap-4">
        <button
          onClick={() => setMode("comments")}
          className={`px-4 py-2 rounded ${mode === "comments" ? "bg-blue-500" : "bg-gray-600"}`}
        >
          Comment Generator
        </button>
        <button
          onClick={() => setMode("explain")}
          className={`px-4 py-2 rounded ${mode === "explain" ? "bg-blue-500" : "bg-gray-600"}`}
        >
          Code Explainer
        </button>
      </div>

      {/* Conditional Rendering */}
      {mode === "comments" ? <CommentGenerator /> : <CodeExplainer />}
    </div>
  );
}

export default App;
