export async function getCodeExplanationSteps(code) {
  const response = await fetch("http://localhost:5000/api/explain-steps", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ codeSnippet: code }),
  });
  return response.json();
}
