import React, { useState } from "react";
import { generateComments, getCodeExplanationSteps } from "./api/ai";

function App() {
  const [code, setCode] = useState("");
  const [result, setResult] = useState("");
  const [mode, setMode] = useState("comments"); // comments or explain
  const [loading, setLoading] = useState(false);


const handleSubmit = async () => {
  if (!code.trim()) return; // empty input block

  setLoading(true);  // ✅ start loader
  setResult("");     // clear previous result

  try {
    if (mode === "comments") {
      const data = await generateComments(code);
      setResult(data.comments);
    } else {
      const data = await getCodeExplanationSteps(code);
      setResult(data.explanation);
    }
  } catch (err) {
    setResult("❌ Error generating response. Try again!");
    console.error(err);
  }

  setLoading(false); // ✅ stop loader
};


  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4 text-center">AI Code Helper</h1>

      <div className="flex gap-4 mb-4 justify-center">
        <button onClick={() => setMode("comments")} className={`px-4 py-2 rounded ${mode === "comments" ? "bg-blue-500 text-white" : "bg-gray-200"}`}>
          Generate Comments
        </button>
        <button onClick={() => setMode("explain")} className={`px-4 py-2 rounded ${mode === "explain" ? "bg-blue-500 text-white" : "bg-gray-200"}`}>
          Explain Steps
        </button>
      </div>

      <textarea
        className="w-full border rounded p-2 mb-4 h-32"
        placeholder="Paste your code here..."
        value={code}
        onChange={(e) => setCode(e.target.value)}
      />

      <button onClick={handleSubmit} className="px-6 py-2 bg-green-500 text-white rounded hover:bg-green-600 mb-4">
        Submit
      </button>

      <div className="bg-gray-100 p-4 rounded min-h-[150px] whitespace-pre-wrap">
  {loading ? "⏳ Generating..." : result || "Result will appear here..."}
</div>

    </div>
  );
}

export default App;
