export const getCodeExplanationSteps = async (codeSnippet) => {
  const res = await fetch("http://localhost:5000/api/explain-steps", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ codeSnippet }),
  });
  return await res.json();
};
